export {login, logout, register} from './actions'

const initialState: stateValue = {
    user: {
        id: 0,
        fullname: "",
        email: "",
        password: "",
        isLogged: false,
        isRegistered: false,
        draftBlogs: [{
            id: 0,
            title: "",
            body: "",
            shouldPublish: false,
        }]
    }
}


export default function loginReducer(state:stateValue = initialState, action): stateValue  {
    switch(action.type) {
        case 'LOGIN' : return { 
                            isLogged: true
                         };
        case 'LOGOUT' : return { isLogged: false};
        default: return state;
    }
}
