import { Login, Logout } from "../actionTypes";

const initialState = {
    isLogged: false,
}

export default function loginReducer(state = initialState, action)  {
    switch(action.type) {
        case 'LOGIN' : return {
                            ...state,
                            isLogged: true};
        case 'LOGOUT' : return {
                            ...state,
                            isLogged: false};
        default: return state;
    }
}
