import { LoginStart, LoginFailure, LoginSuccess, Logout, Register } from "../actionTypes";
import { UserType } from "../../types.d";

const initialState: UserType = {
    user: {
        email: "",
        username: "",
    },
    isLogged: false,
    isRegistered: false,
}

export default function loginReducer(state = initialState, action: { type: string}): UserType  {
    switch(action.type) {
        case Login : return {
                            ...state,
                            isLogged: true
                        }
        case Logout : return {
                            ...state,
                            isLogged: false
                        }
        case Register: return {
                            ...state,
                            isRegistered: true
                        }
        default: return state;
    }
}
