import React from 'react'
import image from '../images/backgro.jpeg'

export default class Post extends React.Component {

  constructor(props)
  {
    super(props);
    this.state = {
      index: this.props.index,
      post: this.props.post,
    }
  }
  render()
  {

    return (
      <div className="post">
        <img src={this.state.post.imageUrl} className="post-image" alt="" />
        <div className='post-info'>
          <span className="post-title">{this.state.post.title}</span>
          <hr />
          <span className="post-date">{this.state.post.time}</span>
        </div>
        <p className="post-desc">{this.state.post.body}</p>
      </div>
    )
  }
}
