import React, { useState } from 'react'
import '../../index.scss'
import profile from '../../images/admin2.png'
import { useSelector } from 'react-redux';
import store from '../../store/store';
import axios from 'axios'; 

export default function Settings() {
    const user = useSelector(state => state.login.user);
    const [username, setUsername] = useState("");
    const [profileURL, setProfileURL] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleUpdate = async (event) => {
        event.preventDefault();
        const newUser = {
            userId: user._id,
            username, email, password, profilePic: profileURL,
        }
        try {
            const res = await axios.put(`/user${user._id}`, newUser);
            console.log(res);
        }
        catch(err) {
            console.log(err);
        }
    }


    return (
    <div className='settings'>
        <div className="settings-wrapper">
            <div className="settings-title">
                <span className="settings-update-title"><b>Update Your Account</b></span>
            </div>
            <i 
                className="settings-delete-title fa-solid fa-trash-can" 
                // onClick={handleDelete}
            ></i>
            <form className="settings-form">
                <div className="settings-inn">
                    <div className="settingsPP">
                        <img src={user.profilePic ? user.profilePic : profile} alt="" />
                        <label htmlFor="file-input">
                            {/* <i className="settings-icon fa-solid fa-user-large"></i> */}
                        </label>
                        <input type="file" id='file-input' style={{display: "none"}} />
                    </div>
                    <input 
                        type="email"
                        className='settings-input'
                        placeholder={user.email}
                        // value={}
                        // onChange={}
                        />

                    <input 
                        type="text" 
                        className="settings-input" 
                        placeholder='Full Name'
                        // value={}
                        // onChange={}
                        />

                    <input 
                        type="text" 
                        className="settings-input" 
                        placeholder='Profile Image URL'
                        // value={}
                        // onChange={}
                        />

                    <input 
                        type="text" 
                        className='settings-input'
                        placeholder='Username'
                        // value={}
                        // onChange={}
                        />
                    <input 
                        type="password" 
                        className='settings-input'
                        placeholder='Password'
                        // value={}
                        // onChange={}
                        />
                    <button className='settings-submit'>Update</button>
                </div>
            </form>
        </div>  
    </div>
  )
}
