import React, { useState } from 'react'
import '../../index.scss'
import profile from '../../images/admin2.png'
import { useSelector } from 'react-redux';
import store from '../../store/store';
import axios from 'axios'; 

export default function Settings() {
    const user = useSelector(state => state.login.user);
    const [file, setFile] = useState(null);
    const [username, setUsername] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");

    const handleUpdate = async (event) => {
        event.preventDefault();
        const newUser = {
            userId: user._id,
            username, email, password, 
        }
        try {
            // const res = await axios.post("/blogs", newBlog);
            // console.log(res);
        }
        catch(err) {
            console.log(err);
        }
        this.setState({
            [event.target.name]: event.target.value,
            isPublished: true,
            gotoHome: true
        });
    }


    return (
    <div className='settings'>
        <div className="settings-wrapper">
            <div className="settings-title">
                <span className="settings-update-title">Update Your Account</span>
                <span className="settings-delete-title">Delete Your Account</span>
            </div>
            <form className="settings-form">
                {/* <label htmlFor="profile">Profile Picture</label> */}
                <div className="settingsPP">
                    <img src={user.profilePic ? user.profilePic : profile} alt="" />
                    <label htmlFor="file-input">
                        {/* <i className="settings-icon fa-solid fa-user-large"></i> */}
                    </label>
                    <input type="file" id='file-input' style={{display: "none"}} />
                </div>
                <input 
                    type="email"
                    className='settings-input'
                    placeholder='Email'
                    // value={}
                    // onChange={}
                />

                <input 
                    type="text" 
                    className="settings-input" 
                    placeholder='Full Name'
                    // value={}
                    // onChange={}
                />

                <input 
                    type="text" 
                    className="settings-input" 
                    placeholder='Profile Image URL'
                    // value={}
                    // onChange={}
                />

                <input 
                    type="text" 
                    className='settings-input'
                    placeholder='Username'
                    // value={}
                    // onChange={}
                />
                <input 
                    type="password" 
                    className='settings-input'
                    placeholder='Password'
                    // value={}
                    // onChange={}
                />
                <button className='settings-submit'>Update</button>
            </form>
        </div>  
    </div>
  )
}
