import store from "../store/store";
import axios, { AxiosResponse } from "axios";

export const fetchBlogs = (search: any) => {
                        search ? 
                        axios.get(`/blog/${search}`) :
                        axios.get("/blogs/")
                    }


export const fetchBlogbyId = (id: any) => axios.get(`/blogs/${id}`);
