import React from 'react'
import '../../index.scss'
import { Link } from 'react-router-dom'
import { login, logout } from '../../store/actions'
import { connect } from 'react-redux'

const mapStateToProps = props => {
    return {
      isLogged: props.loginReducer
    }
}

const mapDispatchToProps = (dispatch) => {
  return {
      login: () => dispatch(login()),
      // logout: () => dispatch(logout())
  }
}
export default class Login extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: "",
      isLogged: this.props.isLogged
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleLogin = this.handleLogin.bind(this);
  }


  handleLogin(event) {
    // Below function prevents page from getting refreshed after submit
    event.preventDefault();
    // this.setState(() => {
    //   return {
    //     // this feature is inrtoduced in ES6 where we can give value to  objects's props using []
    //     [event.target.name]: event.target.value,
    //     [event.target.isLogged]: true,
    //   }
    // })
    this.props.login()
  }


  // On change in form
  handleChange(event) {
    this.setState(() => {
      return  {  
        [event.target.name]: event.target.value,
      }
    })
  }


  render() {
    return (
      <div className="login">
        <div className="inner-login">
          <span>{this.state.isLogged ? "Logged" : "Please log"}</span>
          <span className="login-title">Login</span>
          <form className="login-form" 
            // onSubmit={this.handleLogin} 
          >
              <input 
                className="login-input" 
                type="email"
                name="username"
                placeholder='Username'
                value={this.state.username}
                onChange={this.handleChange}
              />
              <input 
                type="password" 
                className="login-input"
                name="password"
                placeholder='Password'
                value={this.state.password}
                onChange={this.handleChange}
              />
              <button 
                className="login-button" 
                // type='submit'
                onClick={() => this.props.login()}
              >Log In</button>
          </form>
        </div>
        <div className="login-inn">
              Do not have an account?<Link className="link" to="/register/">&nbsp;Sign Up</Link>
          </div>
      </div>
    )
  }
}

connect(mapStateToProps, mapDispatchToProps)(Login)
