import React from 'react'
import '../../index.scss'
import { Link } from 'react-router-dom'

export default class Login extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      username: "",
      password: ""
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleLogin = this.handleLogin.bind(this);
  }


  handleLogin(event) {
    // Below function prevents page from getting refreshed after submit
    event.preventDefault();
    this.setState(() => {
      return {
        // this feature is inrtoduced in ES6 where we can give value to  objects's props using []
        [event.target.name]: event.target.value,
        [event.target.isLogged]: true,
      }
    })
  }


  // On change in form
  handleChange(event) {
    this.setState(() => {
      return  {  
        [event.target.name]: event.target.value,
      }
    })
  }


  render() {
    return (
      <div className="login">
        <div className="inner-login">
          <span className="login-title">Login</span>
          <form className="login-form">
              <input 
                className="login-input" 
                type="email"
                name="username"
                placeholder='Username'
                value={this.state.username}

              />
              <input 
                type="password" 
                className="login-input"
                name="password"
                placeholder='Password'
                value={this.state.password}
              />
              <button className="login-button">Log In</button>
          </form>
        </div>
        <div className="login-inn">
              Do not have an account?<Link className="link" to="/register/">Sign Up</Link>
          </div>
      </div>
    )
  }
}
