import React from 'react'
import store from '../store';

export default class User extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            firstName: "",
            lastName: "",
            isLogged: false,
        }

        this.loginStateChange = this.loginStateChange.bind(this);
    }

    loginStateChange() {
        this.setState(prevState => ({
            isLogged: true,
        }));
    }

    render() {
        return (
            <div onClick={this.loginStateChange}>
                <h1>CHANGE</h1>
                <h1>{this.state.isLogged}</h1>
            </div>
    )
  }
}
